mapper.select:
SELECT a.id as applicationId, a.process_name as title, a.business_code as businessCode, a.apply_type as applyType, a.pc_icon as pcIcon, a.effective_source as effectiveSource, a.effective_source as source, a.is_son as isSon, a.look_url as lookUrl, a.submit_url as submitUrl, a.condition_url as conditionUrl, a.parent_id as parentId, a.application_url as applicationUrl, a.route as route, a.process_key as processKey, a.permissions_code as permissionsCode, a.create_user_id as createUserId, t.id as id, t.state as state, t.visble_state as visbleState, t.sort as sort FROM bpm_process_app_application a left join bpm_process_application_type t on t.application_id=a.id WHERE a.is_del = 0 and t.is_del=0
and t.category_id =#{id}
and case when a.business_code is null then a.process_key else concat(a.business_code,"_",a.process_key) end in
#{item}
and t.visble_state =#{visbleState}
and a.is_son =#{isSon}
and a.parent_id =#{parentId}
and a.id in
#{item}
order by t.sort asc

select id, process_key AS processKey, process_name AS processName from bpm_process_app_application where is_del = 0
and process_name like CONCAT("%",#{search},"%")
LIMIT #{limitSize}
