mapper.select:
SELECT a.id AS id, a.bpmn_code AS bpmnCode, a.form_code AS formCode, c.dict_label AS formCodeDisplayName, a.bpmn_name AS bpmnName, a.deduplication_type AS deduplicationType, a.effective_status AS effectiveStatus, a.business_party_id AS businessPartyId, a.update_time AS updateTime, a.is_out_side_process as isOutSideProcess, a.is_lowcode_flow as isLowCodeFlow, a.remark AS remark, tmp.process_key AS noticeFormCode FROM t_bpmn_conf a left join t_out_side_bpm_business_party b on a.business_party_id = b.id left join t_dict_data c on c.dict_value = a.form_code and a.is_lowcode_flow =1 left join (select distinct process_key from bpm_process_notice) tmp on a.form_code = tmp.process_key WHERE a.is_del = 0
AND a.is_out_side_process = #{bpmnConfVo.isOutSideProcess}
AND a.is_lowcode_flow = #{bpmnConfVo.isLowCodeFlow}
AND (a.bpmn_code LIKE CONCAT('%',#{bpmnConfVo.search},'%') OR a.bpmn_name LIKE CONCAT('%',#{bpmnConfVo.search},'%'))
AND a.form_code LIKE CONCAT('%',#{bpmnConfVo.formCode},'%')
AND a.bpmn_code LIKE CONCAT('%',#{bpmnConfVo.bpmnCode},'%')
AND a.bpmn_name LIKE CONCAT('%',#{bpmnConfVo.bpmnName},'%')
AND b.business_party_mark=#{bpmnConfVo.businessPartyMark}
AND a.effective_status = #{bpmnConfVo.effectiveStatus}
AND c.dict_label LIKE CONCAT('%',#{bpmnConfVo.formCodeDisplayName},'%')
ORDER BY a.create_time DESC
