mapper.select:
SELECT a.id AS id, a.bpmn_code AS bpmnCode, a.form_code AS formCode, a.bpmn_name AS bpmnName, a.deduplication_type AS deduplicationType, a.effective_status AS effectiveStatus, a.business_party_id AS businessPartyId, a.update_time AS updateTime, a.is_out_side_process as isOutSideProcess, a.is_lowcode_flow as isLowCodeFlow, a.remark AS remark FROM t_bpmn_conf a left join t_out_side_bpm_business_party b on a.business_party_id = b.id WHERE a.is_del = 0
AND a.is_out_side_process = #{bpmnConfVo.isOutSideProcess}
AND a.is_lowcode_flow = #{bpmnConfVo.isLowCodeFlow}
AND (a.bpmn_code LIKE CONCAT('%',#{bpmnConfVo.search},'%') OR a.bpmn_name LIKE CONCAT('%',#{bpmnConfVo.search},'%'))
AND a.bpmn_code LIKE CONCAT('%',#{bpmnConfVo.bpmnCode},'%')
AND a.bpmn_name LIKE CONCAT('%',#{bpmnConfVo.bpmnName},'%')
AND b.business_party_mark=#{bpmnConfVo.businessPartyMark}
AND a.effective_status = #{bpmnConfVo.effectiveStatus}
ORDER BY a.create_time DESC
